const islandReducer = (state = {islands: [], loading: false}, action) => {
  switch(action.type) {
    case 'FETCH_ISLANDS': 
      return {
        ...state, 
        islands: [...state.islands],
        loading: true
      }
    case 'ISLANDS_FETCHED':
      return {
        ...state,
        islands: action.islands,
        loading: false
      }
    case 'ADD_ISLAND':
      return {...state, islands: [...state.islands, action.payload]}
    case 'DELETE_ISLAND':
      const islands = state.islands.filter(island => island.id !== action.id)
      return {...state, islands}
    case 'ADD_VILLAGER':
      let islandsPlus = state.islands.map(island => {
        if (island.id === action.payload.id) {
          return action.payload
        } else {
          return island
        }
      })
      return {...state, islands: islandsPlus}
    case 'REMOVE_VILLAGER':
      let islandsMinus = state.islands.map(island => {
        if (island.id === action.payload.id) {
          return action.payload
        } else {
          return island
        }
      })
      return {...state, islands: islandsMinus}
    default: 
      return state;
  }

}

export default islandReducer;
