function Villagers({removeVillager, villagers, match}) {

  const handleRemove = (villager, island) => {
    removeVillager(villager, island)
    // window.location.reload(true);
  }

    const list = villagers && villagers.map(villager =>
      <li key={villager.id}> 
        <Link to={`/villagers/${villager.id}`} 
        villagers={villagers} 
        className="link">  
        {villager.name} </Link> 
          <img src={remove} alt="remove villager" 
          onClick={() => {return handleRemove(villager.id, villager.island_id)}} /> 
      </li> )

  return <> {list} </>
}

export default Villagers;
