import React from 'react';
import { connect } from 'react-redux';
import {Route, Switch} from 'react-router-dom';
import {addVillager, removeVillager} from './actions/villagerActions'
import VillagerSearch from '../components/villagers/VillagerSearch';
// import SearchResults from '../components/villagers/SearchResults';
import Villagers from '../components/villagers/Villagers';
import Villager from '../components/villagers/Villager';

function VillagersContainer({match, villagers, islands, addVillager, removeVillager}) {
  const villager = villagers && villagers.find(villager => 
    villager.id == match.params.id)

  const island = islands.find(island => island.id == match.params.id);

  const handleAdd = (villager, island) => {
    addVillager(villager, island);
  }

  const handleRemove = (villager, island) => {
    removeVillager(villager, island);
  }

    return (
      <>
        <Switch>
          <Route exact path="/villagers" render={(routerProps) => <Villagers {...routerProps} villagers={villagers} island={island} handleRemove={handleRemove} />} />
          <Route path={`/villagers/:id`}
            render={(routerProps) => 
            <Villager {...routerProps} 
                villagers={villagers} />}/>
          <Route path="/search" render={(routerProps) => 
            <VillagerSearch {...routerProps} villagers={villagers} 
            island={island} 
            handleAdd={handleAdd} />} />         
        </Switch>


      </>
    )
  }

const mapStateToProps = state => {
  return {
    villagers: state.villagerReducer.villagers, 
    islands: state.islandReducer.islands
  }
}

export default connect(mapStateToProps, {addVillager, removeVillager})(VillagersContainer);
