export function fetchVillagers() {
  return (dispatch) => {
    dispatch({type: 'FETCH_VILLAGERS'})
    fetch('http://localhost:3000/villagers')
    .then(resp => resp.json())
    .then(data => dispatch({
      type: 'VILLAGERS_FETCHED',
      villagers: data
    })
    )
  }
}



export const addVillager = (villagerId, islandId) => {
  return (dispatch) => {
    fetch(`http://localhost:3000/villagers/${villagerId}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        island_id: islandId
      })
    })
    .then(resp => resp.json())
    .then(villager => dispatch({
      type: 'ADD_VILLAGER', 
      payload: villager})
    )
  }
}

export const removeVillager = (villagerId) => {
  return (dispatch) => {
    fetch(`http://localhost:3000/villagers/${villagerId}`, {
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      method: 'PATCH',
      body: JSON.stringify({
        island_id: ''
      })
    })
    .then(villager => dispatch({type: 'REMOVE_VILLAGER', payload: villager}))
  }
}

export const buttonType = (villager, island) => {
  if (villager.island_id === island.id) {
    return (<Button 
      size="sm" 
      variant="light"
      onClick={() => handleRemove(villager.id, villager.island_id)} > x </Button>)
  } else if (villager.island_id !== island.id && island.villagers.count > 10) {
    return (<Button size="sm" 
      variant="light"
      onClick={() => handleAdd(villager.id, island.id)}> Add Villager </Button>)
  }
}
