import React, { useState } from 'react';
import { Col } from 'react-bootstrap';
import { connect } from 'react-redux';
import {addVillager} from '../../actions/villagerActions';
import SearchResults from './SearchResults'

function VillagerSearch({villagers}) {
  const [query, setQuery] = useState('')

  const searchResults = () => {
    return villagers && villagers.filter(villager => villager.name.toLowerCase().includes(query.toLowerCase())) 
    }

    const handleAdd = (villager, island) => {
      addVillager(villager, island);
    }

   const displayResults = () => {
    const results = searchResults();
      if (query === '') {
        return ''
      } else {
        return <SearchResults villagers={results} handleAdd={handleAdd} />
      }
    }

    return (
      <Col className="column card">
        <input type="text" 
          className="searchbar"
          placeholder="Search villager by name..." 
          value={query} 
          onChange={(e) => setQuery(e.target.value)} />

        {displayResults()}
      </Col>
    )
  }

  // const mapStateToProps = (state) => {
  //   return {
  //     villagers: state.villagerReducer.villagers,
  //     islands: state.islandReducer.islands
  //   }
  // }


export default connect(mapStateToProps, {addVillager})(VillagerSearch);
