const villagerReducer = (state = { villagers: [], loading: false }, action) => {
  switch(action.type) {
    case 'LOADING_VILLAGER':
      return {
        ...state, 
        villagers: [...state.villagers],
        loading: true
      }
      case 'SHOW_VILLAGER': {

      }
  }
}

export default villagerReducer;
