import React from 'react';
import { connect } from 'react-redux';
import {Route, Switch} from 'react-router-dom';
import {fetchIslands} from '../actions/islandActions';
import {deleteIsland} from '../actions/islandActions'
import Navbar from '../components/Navbar'
import Island from '../components/islands/Island'
import Islands from '../components/islands/Islands';
import IslandInput from '../components/islands/IslandInput';

class IslandsContainer extends React.Component {

  componentDidMount() {
    this.props.fetchIslands()
  }

  handleDelete(islandId) {
    this.props.deleteIsland(islandId)
    this.props.his
  }

  render() {
    return (
      <div>
        <Navbar />
        <Switch>
          <Route path='/islands/new' 
            component={IslandInput} />

          <Route exact path='/islands/:id'
            render={(routerProps) => 
              <Island {...routerProps} 
                islands={this.props.islands} 
                deleteIsland={this.props.deleteIsland}/>}/>

          <Route path='/islands' 
            render={(routerProps) => 
              <Islands {...routerProps} 
                islands={this.props.islands} />} />
        </Switch>
      </div>
    )
  }


}

const mapStateToProps = state => {
  return {
    islands: state.islandReducer.islands
  }
}

export default connect(mapStateToProps, {fetchIslands, deleteIsland})(IslandsContainer);
