import React from 'react';
import { Link, Route } from 'react-router-dom'
import { connect } from 'react-redux'
import {removeVillager} from '../../actions/villagerActions'
import Villager from './Villager'

function Villagers({villagers}) {

  const handleRemove = (villager) => {
    removeVillager(villager.id)
  }

  const list = villagers && villagers.map(villager =>
    <li key={villager.id}> <Link to={`/villagers/${villager.id}`} villagers={villagers}> {villager.name} </Link> <button onClick={() => handleRemove(villager.id)} > x </button> </li>)
  return (
    <>
    <Route path="/villagers/:id" render={(routerProps) => <Villager {...routerProps} villagers={villagers} />} />
    <ul>
      {list}
    </ul>
    </>
  )
}

const mapStateToProps = (state) => {
  return {
    villagers: state.VillagerReducer.villagers
  }
}

export default connect(mapStateToProps, {removeVillager})(Villagers);
