class IslandInput extends React.Component {
  state = {
    name: '',
    owner: ''
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.addIsland(this.state)
    this.setState({
      name: ''
    })
  }


}

export default IslandInput;
