export default function islandReducer(state = { islands: []}, action) {
  switch(action.type) {
    case 'FETCH_ISLANDS': 
    return {islands: action.payload}
    case 'ADD_ISLAND':
      return {...state, islands: [...state.islands, action.paylo]}
  }
}
