import React from 'react';
import { connect } from 'react-redux';
import {Route, Switch} from 'react-router-dom';
import Villagers from '../Villagers'
import Villager from '../components/villagersVillager'


function VillagersContainer({match, villagers}) {

    return (
      <Switch>
        <Route path="/villagers/:id" render={(routerProps) => <Villagers {...routerProps} villagers={villagers} />} />
        <Route exact path={`/villagers/:id`}
            render={(routerProps) => 
              <Villager {...routerProps} 
                villagers={villagers} />}/>
      </Switch>
    )
  }

const mapStateToProps = state => {
  return {
    villagers: state.villagerReducer.villagers
  }
}

export default connect(mapStateToProps)(VillagersContainer);
