import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Route, Switch } from 'react-router-dom'
import {fetchIslands} from './actions/islandActions';
import {fetchVillagers} from './actions/villagerActions';
import {addVillager} from '../actions/villagerActions';
import SearchBar from '../components/search/SearchBar';
// import SearchResults from '../components/search/SearchResults';

function SearchContainer(addVillager, villagers) {
  const [query, setQuery] = useState('');

  useEffect(() => {
    fetchIslands();
    fetchVillagers();
  },[fetchIslands, fetchVillagers])

  return (
    <> 
      <Switch>
      <Route exact path={'/search'} 
      render={(routerProps) => 
        <SearchBar {...routerProps} 
        villagers={villagers}
        query={query} 
        setQuery={setQuery} /> } />
      </Switch>
      {/* <SearchBar query={query} setQuery={setQuery} villagers={villagers}  /> */}
    </>
  )
}

  const mapStateToProps = state => {
    return {
      villagers: state.villagers,
      islands: state.islands
    }
  }

export default connect(mapStateToProps, {addVillager, fetchIslands, fetchVillagers})(SearchContainer);
